    drawboxthinborder() {
        if (this.w > 0 && this.h > 0) {
            let realbordersize = 1;

            /* clear it */
            this.dr(this.bx, this.by, this.w, this.h, cW);

            /* draw borders */
            this.dr(this.bx, this.by, this.w, realbordersize, cB);
            this.dr(this.bx, this.by, realbordersize, this.h, cB);
            this.dr(
                this.bx,
                this.by + this.h - realbordersize,
                this.w,
                realbordersize,
                cB
            );
            this.dr(
                this.bx + this.w - realbordersize,
                this.by,
                realbordersize,
                this.h,
                cB
            );
        }
    }

