    drawCorners(corner: string) {
        assertEq(5, this.w, '3D|');
        assertEq(5, this.h, '3C|');
        if (corner === 'nw') {
            this.dr(this.bx, this.by + 0, 5, 1, cB);
            this.dr(this.bx, this.by + 1, 3, 1, cB);
            this.dr(this.bx, this.by + 2, 2, 1, cB);
            this.dr(this.bx, this.by + 3, 1, 1, cB);
            this.dr(this.bx, this.by + 4, 1, 1, cB);
        } else if (corner === 'sw') {
            this.dr(this.bx, this.by + 0, 1, 1, cB);
            this.dr(this.bx, this.by + 1, 1, 1, cB);
            this.dr(this.bx, this.by + 2, 2, 1, cB);
            this.dr(this.bx, this.by + 3, 3, 1, cB);
            this.dr(this.bx, this.by + 4, 5, 1, cB);
        } else if (corner === 'ne') {
            this.dr(this.bx + 0, this.by + 0, 5, 1, cB);
            this.dr(this.bx + 2, this.by + 1, 3, 1, cB);
            this.dr(this.bx + 3, this.by + 2, 2, 1, cB);
            this.dr(this.bx + 4, this.by + 3, 1, 1, cB);
            this.dr(this.bx + 4, this.by + 4, 1, 1, cB);
        } else if (corner === 'se') {
            this.dr(this.bx + 4, this.by + 0, 1, 1, cB);
            this.dr(this.bx + 4, this.by + 1, 1, 1, cB);
            this.dr(this.bx + 3, this.by + 2, 2, 1, cB);
            this.dr(this.bx + 2, this.by + 3, 3, 1, cB);
            this.dr(this.bx + 0, this.by + 4, 5, 1, cB);
        }
    }
